import { useState, useEffect } from "react";
import { Button, Form, FormGroup, Input, Label } from "reactstrap";
import axios from "axios";
import { CAR_API_URL } from "../constants";
import { useAppContext } from "../context/AppContext";

const NewCarForm = (props) => {
  const initialData = { id: 0, name: "", email: "", document: "", number: "" };
  const [formData, setFormData] = useState(initialData);
  const { getcars } = useAppContext();

  useEffect(() => {
    if (props.car) {
      const { id, name, email, document, number } = props.car;
      setFormData({ id, name, email, document, number });
    }
  }, [props.car]);

  const handleChange = (e) =>
    setFormData((prev) => ({ ...prev, [e.target.name]: e.target.value }));

  const handleAddUser = async (e) => {
    e.preventDefault();
    try {
      const resp = await axios.post(CAR_API_URL, formData);
      if ((resp.statusText = "OK")) {
        setFormData(initialData);
        props.toggle();
        getCars();
      }
    } catch (err) {
      console.log(err);
    }
  };

  const handleEdiCar = async (e) => {
    e.preventDefault();
    try {
      const resp = await axios.put(
        `${CAR_API_URL}/${props.car.id}`,
        formData
      );
      if ((resp.statusText = "OK")) {
        setFormData(initialData);
        props.toggle();
        getCars();
      }
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <Form onSubmit={props.car ? handleEditCar : handleAddCar}>
      <FormGroup>
        <Label for="name">Name:</Label>
        <Input
          type="text"
          name="name"
          required
          onChange={handleChange}
          value={formData.name}
        />
      </FormGroup>
      <FormGroup>
        <Label for="email">Email:</Label>
        <Input
          type="email"
          name="email"
          required
          onChange={handleChange}
          value={formData.email}
        />
      </FormGroup>
      <FormGroup>
        <Label for="document">Document:</Label>
        <Input
          type="text"
          name="document"
          required
          onChange={handleChange}
          value={formData.document}
        />
      </FormGroup>
      <FormGroup>
        <Label for="number">Number:</Label>
        <Input
          type="number"
          name="number"
          required
          onChange={handleChange}
          value={formData.number}
        />
      </FormGroup>
      <Button>Send</Button>
    </Form>
  );
};
export default NewCarForm;
